sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}


	var ga = new GlideAjax('customPriorityLookup');
	ga.addParam('sysparm_name', 'customPriorityLookup');
	ga.addParam('sysparm_name_category', g_form.getValue('category'));
	ga.addParam('sysparm_subcategory', g_form.getValue('subcategory'));
	ga.getXML(getPriority);
	//ga.getXMLAnswer(getPriority);


	function getPriority(response) {
		var number = response.responseXML.documentElement.getAttribute("answer");
		//var number = response;

		if (number == 1 || number == 2 || number == 3 || number == 4)
		g_form.setValue('priority', number);
		//alert(number);


	}

}
