sys_script_include.script:
var customPriorityLookup = Class.create();
customPriorityLookup.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	customPriorityLookup : function()
	{
		var category = this.getParameter('sysparm_name_category');
		var sub_category = this.getParameter('sysparm_subcategory');

		var gr = new GlideRecord('x_738708_custom_0_custom_priority_lookup');
		gr.addQuery('category',category);
		gr.addQuery('subcategory',sub_category);
		gr.query();
		while(gr.next())
			{

				//gs.info('This is a test log: ' + gr.sys_id);
				return gr.priority;

		}

	},

    type: 'customPriorityLookup'
});
