var key = email.subject;
var splittotext=key.split('');
for(i=0;i<splittotext.length;i++)
{
var chkintable=new GlideRecord('x_738708_custom_0_test_custom');
//var a = chkintable.addJoinQuery('x_738708_custom_0_custom_priority_lookup');
chkintable.addEncodedQuery('app'+splittotext[i]);//replae name with keyword fieldname
chkintable.initialize();

chkintable.query();


if(chkintable.next())

{
  current.category.setDisplayValue(chkintable.category);
  current.subcategory.setDisplayValue(chkintable.subcategory);
current.keyword.setDisplayValue(chkintable.keyword);

}
chkintable.insert();
